package com.yi.kotlin.baseimport com.chad.library.adapter.base.BaseQuickAdapterimport com.chad.library.adapter.base.BaseViewHolder/** * @author Yi * @date 2020/4/17 */open abstract class AppBaseAdapter<T, K : BaseViewHolder>(layoutId: Int) :    BaseQuickAdapter<T, K>(layoutId) {    fun setData(list: MutableList<T>?, reset: Boolean = false) {        if (reset) {            setNewData(list)        } else if (null != list) {            addData(list)        }    }}