package com.yi.kotlin.baseimport android.view.Viewimport com.chad.library.adapter.base.BaseQuickAdapterimport com.chad.library.adapter.base.BaseViewHolder/** * @author Yi * @date 2020/4/17 */abstract class AppBaseAdapter<T, K : AppBaseViewHolder<T>>(layoutId: Int) :    BaseQuickAdapter<T, K>(layoutId) {    fun setData(list: MutableList<T>?, reset: Boolean = false) {        if (reset) setNewData(list)        else if (null != list) addData(list)    }    override fun convert(helper: K, item: T) = helper.setData(item)}abstract class AppBaseViewHolder<T>(view: View) : BaseViewHolder(view) {    abstract fun setData(data: T)}